int main(int argc, char** argv) {
  int id;
  int complte;
  tts_callback_t func = {
    onStart, onCancel, onComplete, onError
  };
  tts_init();
  tts_set(&func);
  id = tts_speak((const char *)argv[1], &complte);
  return 0;
}

